<?phpuse Elementor\Widget_Base;use Elementor\Controls_Manager;/** * ht leadcon Hello World * * Elementor widget for hello world. */class leadcon_Inner_Slider extends Widget_Base {    /**     * Retrieve the widget name.     *     * @return string Widget name.     */    public function get_name() {        return 'leadcon-inner-slider';    }    /**     * Retrieve the widget title.     *     * @return string Widget title.     */    public function get_title() {        return esc_html__( 'Inner Slider', 'leadcon' );    }    /**     * Retrieve the widget icon.     *     * @return string Widget icon.     */    public function get_icon() {        return 'eicon-slider-full-screen';    }    /**     * Retrieve the list of categories the widget belongs to.     *     * Used to determine where to display the widget in the editor.     *     * @return array Widget categories.     */    public function get_categories() {        return array( 'leadcon' );    }    /**     * Retrieve the list of scripts the widget depended on.     *     * Used to set scripts dependencies required to run the widget.     *     * @return array Widget scripts dependencies.     */    public function get_script_depends() {        return array( 'leadcon-inner-slider' );    }    /**     * Register the widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     */    protected function _register_controls() {        $this->start_controls_section(            'section_inner',            array(                'label' => esc_html__( 'Inner', 'leadcon' ),            )        );        $repeater = new Elementor\Repeater();        $repeater->add_control(            'image',            [                'label' => esc_html__( 'Image', 'leadcon' ),                'type' => Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ]            ]        );        $repeater->add_control(            'name',            [                'label' => esc_html__( 'Name', 'leadcon' ),                'type' => Controls_Manager::TEXT,                'placeholder' => esc_html__( 'Enter Name', 'leadcon' ),            ]        );                $this->add_control(            'inner',            [                'label' => esc_html__( 'inner', 'leadcon' ),                'type' => Controls_Manager::REPEATER,                'fields' => $repeater->get_controls(),                'default' => [                    [],                    []                ],                'title_field' => '{{{ name }}}'            ]        );        $this->end_controls_section();        $this->start_controls_section(            'section_layout',            [                'label' => esc_html__( 'Layout', 'leadcon' )            ]        );        $this->add_group_control(            Elementor\Group_Control_Image_Size::get_type(),            [                'name' => 'img'            ]        );        $this->end_controls_section();        $this->end_controls_section();    }    /**     * Render the widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     */    protected function render() {        $settings = $this->get_settings_for_display();        ?>        <div class="leadcon-inner-slider">            <div class="inner-slider-wrapper" >                <div class="js-inner-slider inner-slider-list">                    <?php foreach ($settings['inner'] as $inner ): ?>                        <div class="inner-slider-item">                            <?php                                if( ! $inner['image']['id'] ){                                    $image_url = $inner['image']['url'];                                }                                else{                                    $image_url = Elementor\Group_Control_Image_Size::get_attachment_image_src( $inner['image']['id'], 'img', $settings );                                }                            ?>                            <div class="inner">                                <img src="<?php echo esc_url( $image_url ) ?>" class='image' alt='<?php echo $inner['name']; ?>'>                            </div>                        </div>                    <?php endforeach ?>                </div>            </div>        </div>        <?php    }}