<?php/** * WooCommerce Template tags. * * @package leadcon */if ( ! function_exists( 'leadcon_header_cart' ) ) :    /**     * Display a link to the cart including the number of items present     */    function leadcon_header_cart() {        if ( ! leadcon_is_woocommerce_activated() ) {            return;        }        ?>        <div class="site-header-cart">            <a class="cart-contents" href="<?php echo esc_url( wc_get_cart_url() ); ?>" title="<?php esc_attr_e( 'View your shopping cart', 'leadcon' ); ?>">                <span class="icon-shopping-bag"></span>                <?php /* translators: %d: number of items in cart */ ?>                <span class="count"><?php echo wp_kses_post( sprintf( _n( '%d <span class="screen-reader-text">item</span>', '%d <span class="screen-reader-text">items</span>', WC()->cart->get_cart_contents_count(), 'leadcon' ), WC()->cart->get_cart_contents_count() ) ); ?></span>            </a>        </div><!-- .site-header-cart -->        <?php    }endif;